/*
   Assert - tests the boolean Condition, if it fails then HALT is called.
*/

extern "C" void Assertion_Assert (bool Condition)
{
  if (! Condition)
    {
      StrIO_WriteString ((const char *) "assert failed - halting system", 30);
      StrIO_WriteLn ();
      M2RTS_HALT (-1);
      __builtin_unreachable ();
    }
}

