/*
   IncTime - left := left + right.
*/

static void IncTime (Selective_Timeval left, Selective_Timeval right)
{
  unsigned int lsec;
  unsigned int lusec;
  unsigned int rsec;
  unsigned int rusec;

  Selective_GetTime (left, &lsec, &lusec);
  Selective_GetTime (right, &rsec, &rusec);
  if ((lusec+rusec) < MICROSEC)
    {
      /* No carry  */
      lusec += rusec;
      lsec += rsec;
    }
  else
    {
      lusec += rusec;
      lusec -= MICROSEC;
      lsec += rsec+1;
    }
  Selective_SetTime (left, lsec, lusec);
}


/*
   DecTime - left := left - right.
*/

static void DecTime (Selective_Timeval left, Selective_Timeval right)
{
  unsigned int lsec;
  unsigned int lusec;
  unsigned int rsec;
  unsigned int rusec;

  Selective_GetTime (left, &lsec, &lusec);
  Selective_GetTime (right, &rsec, &rusec);
  if (lusec >= rusec)
    {
      /* No borrow.  */
      lusec -= rusec;
      if (lsec >= rsec)
        {
          lsec -= rsec;
        }
      else
        {
          lsec = 0;
        }
    }
  else
    {
      if (lsec > 0)
        {
          lusec += MICROSEC;
          lusec -= rusec;
          lsec -= 1;
          if (lsec >= rsec)
            {
              lsec -= rsec;
            }
          else
            {
              lsec = 0;
            }
        }
      else
        {
          lsec = 0;
          lusec = 0;
        }
    }
  Selective_SetTime (left, lsec, lusec);
}


