          case M2Diagnostic_timediag:
            d->tdiag.count = 0;
            d->tdiag.total = Selective_InitTime (0, 0);
            d->tdiag.enter = Selective_InitTime (0, 0);
            d->tdiag.exit_ = Selective_InitTime (0, 0);
