  wlists_wlist l;

  mcDebug_assert (decl_isOptarg (static_cast<decl_node> (n)));
  ptype = static_cast<decl_node__opaque> (decl_getType (static_cast<decl_node> (n)));
  mcDebug_assert (n->optargF.namelist != NULL);
